import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {jwtDecode} from 'jwt-decode'; // <-- 1. IMPORT KIYA
import './ItemsList.css'; 
import './StaticPage.css';
import './LoginPage.css'; 

function ItemsListPage() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(''); // Success message ke liye
  const navigate = useNavigate();

  const [searchTerm, setSearchTerm] = useState('');
  const [itemTypeFilter, setItemTypeFilter] = useState('All');
  const [categoryFilter, setCategoryFilter] = useState('All');
  
  // --- 2. NAYA STATE: User ka role store karne ke liye ---
  const [userRole, setUserRole] = useState('student'); 

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setError('You must be logged in to view items.');
          setLoading(false);
          navigate('/login');
          return;
        }

        // --- 3. NAYA LOGIC: Token ko decode karke role nikaala ---
        try {
          const decodedToken = jwtDecode(token);
          setUserRole(decodedToken.user.role);
        } catch (err) {
          console.error("Invalid token");
        }
        // --------------------------------------------------

        const config = { headers: { 'x-auth-token': token } };
        const response = await axios.get('http://localhost:5000/api/items/all', config);
        setItems(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch items. Please try again.');
        console.error(err);
        setLoading(false);
      }
    };
    fetchItems();
  }, [navigate]);

  // (Filter logic waisa hi hai)
  const filteredItems = items.filter(item => {
    const matchesSearch = 
      item.itemName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      item.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = 
      itemTypeFilter === 'All' || item.itemType === itemTypeFilter;
    const matchesCategory = 
      categoryFilter === 'All' || item.category === categoryFilter;
    return matchesSearch && matchesType && matchesCategory;
  });

  // (Normal user ke claim functions waise hi hain)
  const handleClaim = (item) => { /* ... (aapka purana alert code) ... */ };
  const handleFoundIt = (item) => { /* ... (aapka purana alert code) ... */ };


  // --- 4. NAYA FUNCTION: Admin ke liye item 'Claim' (remove) karna ---
  const handleAdminMarkClaimed = async (itemId) => {
    if (!window.confirm('Are you sure you want to mark this item as "Claimed"? It will be removed from this list.')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const config = { headers: { 'x-auth-token': token } };

      // Nayi backend API ko call kiya
      const response = await axios.put(
        `http://localhost:5000/api/admin/claim-item/${itemId}`,
        {},
        config
      );

      setSuccess(response.data.msg); // "Item marked as Claimed"
      
      // Item ko list se (frontend par) hata do
      setItems(prevItems => prevItems.filter(item => item._id !== itemId));

    } catch (err) {
      setError('Failed to update item status.');
    }
  };
  // --------------------------------------------------------------

  return (
    <div className="static-page-container">
      <div className="static-content-box" style={{ maxWidth: '1000px' }}>
        <h2>All Approved Items</h2>
        <p>This is the list of all lost and found items that have been approved by an admin.</p>
        
        {/* ... (Filter bar waisa hi hai) ... */}
        <div className="filter-bar">
          {/* ... (filter inputs) ... */}
        </div>
        
        {loading && <p>Loading items...</p>}
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>} {/* Success message yahan dikhega */}

        <div className="items-list-container">
          
          {filteredItems.map(item => (
            <div className={`item-card item-card-${item.itemType}`} key={item._id}>
              
              {/* ... (Image logic waisa hi hai) ... */}

              <div className="item-card-content">
                
                {/* --- 5. NAYA 'X' BUTTON (SIRF ADMIN KE LIYE) --- */}
                {userRole === 'admin' && (
                  <button 
                    className="admin-remove-button" 
                    onClick={() => handleAdminMarkClaimed(item._id)}
                  >
                    X
                  </button>
                )}
                {/* ------------------------------------------- */}

                <span className={`item-tag ${item.itemType}`}>
                  {item.itemType}
                </span>
                <h3>{item.itemName}</h3>
                {/* ... (Baaki item details) ... */}
                
                {/* ... (Normal user ke claim buttons) ... */}
              </div>
            </div>
          ))}
          
          {/* ... (Empty message logic waisa hi hai) ... */}
        </div>
      </div>
    </div>
  );
}

export default ItemsListPage;
