import React, { useState } from 'react'; 
// 1. Import 'useNavigate'
import { Link, useNavigate } from 'react-router-dom'; 
import './LoginPage.css';

function SignupPage() { 
  
  // 2. Setup 'useNavigate'
  const navigate = useNavigate(); 

  // --- All our states ---
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [mobile, setMobile] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [verification, setVerification] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false);
  const [error, setError] = useState('');

  // --- Signup Form Submit Logic (Yeh pehle se hai) ---
  const handleSignupSubmit = (event) => {
    event.preventDefault(); 
    if (!email.endsWith('@iitj.ac.in')) {
      setError('Error: Please use your official @iitj.ac.in email address.');
      return; 
    }
    if (password !== confirmPassword) {
      setError('Error: Passwords do not match.');
      return;
    }
    if (verification !== '8') { 
      setError('Error: The verification answer (5 + 3) is incorrect.');
      return;
    }
    setError(''); 
    console.log("All checks passed! Starting OTP sending process...");
    setIsOtpSent(true); 
  };

  // --- 3. NAYA FUNCTION: OTP Submit Logic ---
  const handleOtpSubmit = (event) => {
    event.preventDefault();
    // In a real app, we would check if the OTP is correct.
    // Since we are just on the frontend, we'll assume it's correct.
    console.log("OTP Submitted! Navigating to login page...");

    // YEH HAI MAIN STEP: User ko /login par bhej do
    navigate('/login');
  };

  // --- OTP Form ---
  const renderOtpForm = () => {
    return (
      // 4. Ise 'div' se badal kar 'form' karein aur naya function jodein
      <form className="otp-form-container" onSubmit={handleOtpSubmit}>
        <h4>Verify Your Email</h4>
        <p>An OTP has been sent to your email. Please enter it below.</p>
        
        <div className="input-group">
          <label htmlFor="otp">Enter OTP</label>
          <input type="text" id="otp" placeholder="Enter 6-digit OTP" required />
        </div>
        
        {/* Yeh button ab form ko submit karega */}
        <button type="submit" className="login-button">Submit OTP</button>
        
        <div className="otp-options">
          <a href="#" className="resend-link">Resend OTP</a>
          <button type="button" className="back-button" onClick={() => setIsOtpSent(false)}>
            ← Back
          </button>
        </div>
      </form>
    );
  };

  // --- Signup Form (Yeh pehle se hai) ---
  const renderSignupForm = () => {
    return (
      <form onSubmit={handleSignupSubmit}> 
        
        {/* ... (All your input fields: Name, Email, etc. No change here) ... */}
        <div className="input-group">
          <label htmlFor="name">Full Name</label>
          <input 
            type="text" id="name" placeholder="Enter your full name" 
            value={name} 
            onChange={(e) => { setName(e.target.value); if (error) setError(''); }} 
            required 
          />
        </div>
        <div className="input-group">
          <label htmlFor="email-username">Email / Username (IITJ Email)</label>
          <input 
            type="email" id="email-username" placeholder="e.g., user@iitj.ac.in" 
            value={email} 
            onChange={(e) => { setEmail(e.target.value); if (error) setError(''); }} 
            required 
          />
        </div>
        <div className="input-group">
          <label htmlFor="mobile">Mobile No.</label>
          <input 
            type="tel" id="mobile" placeholder="Enter your 10-digit number" 
            value={mobile} 
            onChange={(e) => { setMobile(e.target.value); if (error) setError(''); }} 
            required 
          />
        </div>
        <div className="input-group">
          <label htmlFor="password">Password</label>
          <input 
            type="password" id="password" placeholder="Create a password" 
            value={password} 
            onChange={(e) => { setPassword(e.target.value); if (error) setError(''); }} 
            required 
          />
        </div>
        <div className="input-group">
          <label htmlFor="confirm-password">Confirm Password</label>
          <input 
            type="password" id="confirm-password" placeholder="Confirm your password"
            value={confirmPassword} 
            onChange={(e) => { setConfirmPassword(e.target.value); if (error) setError(''); }} 
            required 
          />
        </div>
        <div className="input-group verification-group">
          <label htmlFor="verification">Verify you are human: <strong>What is 5 + 3 ?</strong></label>
          <input 
            type="text" id="verification" placeholder="Enter your answer" 
            value={verification} 
            onChange={(e) => { setVerification(e.target.value); if (error) setError(''); }} 
            required 
          />
        </div>
        {/* --- End of input fields --- */}

        {error && (
          <p className="error-message" style={{ textAlign: 'center', marginBottom: '10px' }}>
            {error}
          </p>
        )}
        
        <button type="submit" className="login-button">Sign Up</button>
      </form>
    );
  };

  // --- Main Component Return (Yeh pehle se hai) ---
  return (
    <div className="login-page-container">
      <div className="login-box" style={{ width: '400px' }}> 
        
        {!isOtpSent && <Link to="/" className="back-link">← Back to Home</Link>}
        
        <h2>{isOtpSent ? 'Almost There!' : 'Create Account'}</h2>
        
        {isOtpSent ? renderOtpForm() : renderSignupForm()}
        
        {!isOtpSent && (
          <div className="signup-link">
            <p>Already have an account? <Link to="/login">Log in here</Link></p>
          </div>
        )}
        
      </div>
    </div>
  );
}

export default SignupPage;
