import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './StaticPage.css'; // Hum purani CSS ka istemaal kar lenge

function ReportItemPage() {
  const [formData, setFormData] = useState({
    itemName: '',
    category: 'Electronics', // Default value
    location: '',
    description: '',
    itemType: 'found' // Default value 'found'
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  // Jaise hi user kuch type karega, yeh state ko update karega
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Form submit hone par yeh function chalega
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('Submitting...');

    try {
      // 1. Browser ki memory se Token (I-card) nikaala
      const token = localStorage.getItem('token');
      if (!token) {
        setError('You must be logged in to report an item.');
        return;
      }

      // 2. Axios ke liye 'headers' taiyaar kiye
      const config = {
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': token // Yahan humne gatekeeper ke liye token daala
        }
      };

      // 3. Data ko naye API gate par bhej diya
      const response = await axios.post(
        'http://localhost:5000/api/items/report',
        formData, // Form ka data
        config    // Token waale headers
      );

      // 4. Safal hone par
      console.log(response.data);
      setSuccess('Item reported successfully! Status is Pending.');
      setError('');
      // User ko 2 second baad homepage par bhej do
      setTimeout(() => {
        navigate('/');
      }, 2000);

    } catch (err) {
      // 5. Agar token galat hai ya koi aur error aaya
      setSuccess('');
      if (err.response && err.response.data && err.response.data.msg) {
        setError(err.response.data.msg); // Backend ka error
      } else {
        setError('Failed to report item. Please try again.');
      }
    }
  };

  // Form ka structure
  return (
    <div className="static-page-container">
      <div className="static-content-box" style={{ maxWidth: '600px' }}>
        <h2>Report an Item</h2>
        <p>Fill out the details of the item you lost or found.</p>

        <form onSubmit={handleSubmit} className="login-box" style={{ padding: 0, boxShadow: 'none' }}>
          
          <div className="input-group">
            <label>I... (Lost or Found?)</label>
            <select name="itemType" value={formData.itemType} onChange={handleChange} required>
              <option value="found">Found an Item</option>
              <option value="lost">Lost an Item</option>
            </select>
          </div>

          <div className="input-group">
            <label>Item Name</label>
            <input
              type="text"
              name="itemName"
              value={formData.itemName}
              onChange={handleChange}
              placeholder="e.g., Blue Water Bottle"
              required
            />
          </div>
          
          <div className="input-group">
            <label>Category</label>
            <select name="category" value={formData.category} onChange={handleChange} required>
              <option value="Electronics">Electronics</option>
              <option value="Books">Books / Notes</option>
              <option value="Apparel">Apparel / Bags</option>
              <option value="ID Cards">ID Cards / Keys</option>
              <option value="Other">Other</option>
            </select>
          </div>

          <div className="input-group">
            <label>Location</label>
            <input
              type="text"
              name="location"
              value={formData.location}
              onChange={handleChange}
              placeholder="e.g., Library, 2nd Floor"
              required
            />
          </div>

          <div className="input-group">
            <label>Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="Any other details (color, brand, etc.)"
              rows="4"
            ></textarea>
          </div>

          {/* Error aur Success messages */}
          {error && <p className="error-message" style={{ textAlign: 'center' }}>{error}</p>}
          {success && <p className="success-message" style={{ textAlign: 'center' }}>{success}</p>}

          <button type="submit" className="login-button" style={{ width: '100%', marginTop: '10px' }}>
            Submit Report
          </button>
        </form>
      </div>
    </div>
  );
}

export default ReportItemPage;
