// 1. 'useState' ko import karein (yeh 'switch' banane ke kaam aayega)
import React, { useState } from 'react'; 
import { Link } from 'react-router-dom';
import './LoginPage.css';

function SignupPage() { 
  
  // 2. Humara naya 'switch' (State)
  // 'isOtpSent' humein batayega ki OTP view dikhana hai ya nahi
  // Shuru mein yeh 'false' (band) hai
  const [isOtpSent, setIsOtpSent] = useState(false);

  // 3. Yeh function tab chalega jab "Sign Up" button par click hoga
  const handleSignupSubmit = (event) => {
    // Isse page refresh nahi hoga
    event.preventDefault(); 
    
    // Yahan hum baad mein OTP bhejne ka code likhenge
    console.log("OTP Bhejne ka process shuru...");
    
    // 4. Hum 'switch' ko 'true' (chalu) kar denge
    setIsOtpSent(true); 
  };

  // 5. Yeh naya view hai jo OTP maangega
  const renderOtpForm = () => {
    return (
      <div className="otp-form-container">
        <h4>Verify Your Email</h4>
        <p>An OTP has been sent to your email. Please enter it below.</p>
        
        <div className="input-group">
          <label htmlFor="otp">Enter OTP</label>
          <input type="text" id="otp" placeholder="Enter 6-digit OTP" />
        </div>
        
        <button type="submit" className="login-button">Submit OTP</button>
        
        <div className="otp-options">
          <a href="#" className="resend-link">Resend OTP</a>
          
          {/* Yeh 'Back' button switch ko wapas 'false' kar dega */}
          <button type="button" className="back-button" onClick={() => setIsOtpSent(false)}>
            ← Back
          </button>
        </div>
      </div>
    );
  };

  // 6. Yeh purana Signup form hai
  const renderSignupForm = () => {
    return (
      // 7. Form mein 'onSubmit' joda
      <form onSubmit={handleSignupSubmit}> 
        {/* Baaki sabhi fields waise hi hain */}
        <div className="input-group">
          <label htmlFor="name">Full Name</label>
          <input type="text" id="name" placeholder="Enter your full name" required />
        </div>
        <div className="input-group">
          <label htmlFor="email-username">Email / Username (IITJ Email)</label>
          <input type="email" id="email-username" placeholder="e.g., user@iitj.ac.in" required />
        </div>
        <div className="input-group">
          <label htmlFor="mobile">Mobile No.</label>
          <input type="tel" id="mobile" placeholder="Enter your 10-digit number" required />
        </div>
        <div className="input-group">
          <label htmlFor="password">Password</label>
          <input type="password" id="password" placeholder="Create a password" required />
        </div>
        <div className="input-group">
          <label htmlFor="confirm-password">Confirm Password</label>
          <input type="password" id="confirm-password" placeholder="Confirm your password" required />
        </div>
        <div className="input-group verification-group">
          <label htmlFor="verification">Verify you are human: <strong>What is 5 + 3 ?</strong></label>
          <input type="text" id="verification" placeholder="Enter your answer" required />
        </div>
        
        <button type="submit" className="login-button">Sign Up</button>
      </form>
    );
  };

  return (
    <div className="login-page-container">
      <div className="login-box" style={{ width: '400px' }}> 
        
        {/* Back to Home link tabhi dikhega jab OTP form na dikh raha ho */}
        {!isOtpSent && <Link to="/" className="back-link">← Back to Home</Link>}
        
        <h2>{isOtpSent ? 'Almost There!' : 'Create Account'}</h2>
        
        {/* 8. YAHAN HAI ASLI JAADU! */}
        {/* Agar 'isOtpSent' true hai, toh renderOtpForm dikhao */}
        {/* Varna, renderSignupForm dikhao */}
        {isOtpSent ? renderOtpForm() : renderSignupForm()}
        
        {/* "Already have account" link bhi OTP form ke dauraan chhip jayega */}
        {!isOtpSent && (
          <div className="signup-link">
            <p>Already have an account? <Link to="/login">Log in here</Link></p>
          </div>
        )}
        
      </div>
    </div>
  );
}

export default SignupPage;
