import React from 'react';
// 'Link' ko yahan import karna zaroori hai
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';

// Humare saare pages
import HomePage from './HomePage';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage';
import ContactPage from './ContactPage';
import HelpPage from './HelpPage';
import ReportItemPage from './ReportItemPage';

// Humari main CSS file
import './App.css'; 

function App() {
  return (
    <BrowserRouter>

      {/* --- YEH HISSA AB HAMESHA DIKHEGA --- */}
      {/* 1. TOP TITLE */}
      <header className="top-header">
        <h1>Lost and Found</h1>
      </header>

      {/* 2. NAVIGATION BAR */}
      <nav className="navbar">
        <div className="nav-left">
          <Link to="/">Home</Link>
          {/* Is link ko theek kar diya hai taaki yeh doosre page se bhi kaam kare */}
          <a href="/#about-portal">About</a>
          <Link to="/contact">Contact Us</Link>
          <Link to="/help">Help</Link>
        </div>
        <div className="nav-right">
          <Link to="/login">Login</Link>
          <Link to="/signup">Signup</Link>
        </div>
      </nav>
      {/* ------------------------------------- */}


      {/* --- YEH HISSA PAGE KE HISAAB SE BADLEGA --- */}
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<LoginPage />} />
        <Route path="/signup" element={<SignupPage />} />
        <Route path="/contact" element={<ContactPage />} />
        <Route path="/help" element={<HelpPage />} />
        <Route path="/report-item" element={<ReportItemPage />} />
      </Routes>

    </BrowserRouter>
  );
}

export default App;
