import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ItemsList.css'; // We'll create this file next for styling
import './StaticPage.css'; // Re-use for the container

function ItemsListPage() {
  const [items, setItems] = useState([]); // To store the list of items
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // This runs once when the component loads
  useEffect(() => {
    const fetchItems = async () => {
      try {
        // 1. Get token from storage
        const token = localStorage.getItem('token');
        if (!token) {
          setError('You must be logged in to view items.');
          setLoading(false);
          navigate('/login'); // If no token, send to login
          return;
        }

        // 2. Set up headers with the token
        const config = {
          headers: {
            'x-auth-token': token
          }
        };

        // 3. Call the backend API to get all APPROVED items
        const response = await axios.get(
          'http://localhost:5000/api/items/all',
          config
        );

        // 4. Save the list of items in our state
        setItems(response.data);
        setLoading(false);

      } catch (err) {
        setError('Failed to fetch items. Please try again.');
        console.error(err);
        setLoading(false);
      }
    };

    fetchItems();
  }, [navigate]); // Add navigate as a dependency

  // Alert #1: Jab user 'FOUND' item ko 'CLAIM' karta hai
  const handleClaim = (item) => {
    alert(
      `--- How to Claim This Item ---\n\n` +
      `You are claiming the item: ${item.itemName}\n` +
      `Item ID: ${item._id}\n\n` +
      `Please visit the Main Security Office (Admin Block) with this Item ID and your student/staff ID card to claim it.`
    );
  };

  // Alert #2: Jab user 'LOST' item ko 'FOUND' karta hai
  const handleFoundIt = (item) => {
    alert(
      `--- You Found a Lost Item! ---\n\n` +
      `Item Name: ${item.itemName}\n` +
      `Item ID: ${item._id}\n\n` +
      `Thank you for helping! Please submit this item to the nearest Security Guard or the Main Security Office and show them this Item ID.`
    );
  };

  return (
    <div className="static-page-container">
      <div className="static-content-box" style={{ maxWidth: '1000px' }}>
        <h2>All Approved Items</h2>
        <p>This is the list of all lost and found items that have been approved by an admin.</p>

        {loading && <p>Loading items...</p>}
        {error && <p className="error-message">{error}</p>}

        <div className="items-list-container">
          {/* Loop through each item and display it */}
          {items.map(item => (
            <div className={`item-card item-card-${item.itemType}`} key={item._id}>
              
              {/* --- IMAGE DISPLAY --- */}
              {/* Check if an image exists */}
              {item.image ? (
                // We build the full URL to the backend's 'uploads' folder
                <img 
                  src={`http://localhost:5000/${item.image.replace(/\\/g, '/')}`} 
                  alt={item.itemName} 
                  className="item-card-image" 
                />
              ) : (
                // If no image, show a placeholder
                <div className="item-card-no-image">No Image</div>
              )}

              <div className="item-card-content">
                <span className={`item-tag ${item.itemType}`}>
                  {item.itemType === 'lost' ? 'Lost' : 'Found'}
                </span>
                <h3>{item.itemName}</h3>
                <p><strong>Location:</strong> {item.location}</p>
                <p><strong>Category:</strong> {item.category}</p>
                <p>{item.description}</p>
                {/* --- YEH HAI NAYA DYNAMIC BUTTON --- */}
                {item.itemType === 'found' ? (
                  // Agar item 'found' hai (kisi ko mila tha), toh 'Claim' button dikhao
                  <button 
                    className="claim-button" 
                    onClick={() => handleClaim(item)}
                  >
                    Claim This Item
                  </button>
                ) : (
                  // Agar item 'lost' hai (kisi ka khoya tha), toh 'I Found It' button dikhao
                  <button 
                    className="claim-button" 
                    style={{ backgroundColor: '#f0ad4e' }} // Orange color
                    onClick={() => handleFoundIt(item)}
                  >
                    I Found This Item
                  </button>
                )}
                {/* --- BADLAAV KHATAM --- */}
              </div>
            </div>
          ))}
          
          {/* Show a message if no items are found */}
          {!loading && items.length === 0 && (
            <p style={{ textAlign: 'center' }}>No approved items found at this time.</p>
          )}

        </div>
      </div>
    </div>
  );
}

export default ItemsListPage;
