// STEP 1: Import useState. This is essential for managing component state.
import React, { useState } from 'react'; 
import { Link } from 'react-router-dom';
import './LoginPage.css';

function SignupPage() { 
  
  // STEP 2: Create state variables for EACH form input.
  // This is where we will "remember" the user's data.
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [mobile, setMobile] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [verification, setVerification] = useState('');

  // This state acts as a 'switch' to show/hide the OTP form.
  const [isOtpSent, setIsOtpSent] = useState(false);

  // This state will hold our validation error message.
  const [error, setError] = useState(''); 

  // This function runs when the "Sign Up" button is clicked.
  const handleSignupSubmit = (event) => {
    event.preventDefault(); // This prevents the page from refreshing.

    // --- CHECK 1: Email Validation ---
    if (!email.endsWith('@iitj.ac.in')) {
      setError('Error: Please use your official @iitj.ac.in email address.');
      return; // Stop the function here.
    }

    // --- CHECK 2: Password Match ---
    if (password !== confirmPassword) {
      setError('Error: Passwords do not match.');
      return; // Stop the function here.
    }

    // --- CHECK 3: Math Verification (Jo aapne pakda) ---
    // 'verification' state mein user ka answer hai
    if (verification !== '8') { 
      setError('Error: The verification answer (5 + 3) is incorrect.');
      return; // Stop the function here.
    }

    // --- All checks passed! ---
    setError(''); // Clear any old errors
    console.log("All checks passed! Starting OTP sending process...");
    setIsOtpSent(true); 
  };

  // This function renders (displays) the OTP verification form.
  const renderOtpForm = () => {
    return (
      <div className="otp-form-container">
        <h4>Verify Your Email</h4>
        <p>An OTP has been sent to your email. Please enter it below.</p>
        
        <div className="input-group">
          <label htmlFor="otp">Enter OTP</label>
          <input type="text" id="otp" placeholder="Enter 6-digit OTP" />
        </div>
        
        <button type="submit" className="login-button">Submit OTP</button>
        
        <div className="otp-options">
          <a href="#" className="resend-link">Resend OTP</a>
          
          {/* This "Back" button just flips the 'switch' back to false.
            This will hide the OTP form and show the Signup form again.
            The user's data will still be saved in our state variables.
          */}
          <button type="button" className="back-button" onClick={() => setIsOtpSent(false)}>
            ← Back
          </button>
        </div>
      </div>
    );
  };

  // This function renders the main Signup form.
  const renderSignupForm = () => {
    return (
      <form onSubmit={handleSignupSubmit}> 
        
        {/* STEP 3: Connect each input to its state.
          - 'value={name}' sets the displayed value from our state.
          - 'onChange={(e) => setName(e.target.value)}' updates our state every time the user types.
        */}
        
        <div className="input-group">
          <label htmlFor="name">Full Name</label>
          <input 
            type="text" id="name" placeholder="Enter your full name" 
            value={name} 
            onChange={(e) => setName(e.target.value)} 
            required 
          />
        </div>
        
        <div className="input-group">
          <label htmlFor="email-username">Email / Username (IITJ Email)</label>
          <input 
            type="email" id="email-username" placeholder="e.g., user@iitj.ac.in" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required 
          />
          {error && <p className="error-message">{error}</p>}
        </div>
        
        <div className="input-group">
          <label htmlFor="mobile">Mobile No.</label>
          <input 
            type="tel" id="mobile" placeholder="Enter your 10-digit number" 
            value={mobile} 
            onChange={(e) => setMobile(e.target.value)} 
            required 
          />
        </div>

        <div className="input-group">
          <label htmlFor="password">Password</label>
          <input 
            type="password" id="password" placeholder="Create a password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            required 
          />
        </div>
        
        <div className="input-group">
          <label htmlFor="confirm-password">Confirm Password</label>
          <input 
            type="password" id="confirm-password" placeholder="Confirm your password"
            value={confirmPassword} 
            onChange={(e) => setConfirmPassword(e.target.value)} 
            required 
          />
        </div>
        
        <div className="input-group verification-group">
          <label htmlFor="verification">Verify you are human: <strong>What is 5 + 3 ?</strong></label>
          <input 
            type="text" id="verification" placeholder="Enter your answer" 
            value={verification} 
            onChange={(e) => {
                               setVerification(e.target.value);
                                  if (error) setError(''); 
                              }
        } 
            required 
          />
        </div>
        
        <button type="submit" className="login-button">Sign Up</button>
      </form>
    );
  };

  // This is the main return statement for the component.
  return (
    <div className="login-page-container">
      <div className="login-box" style={{ width: '400px' }}> 
        
        {/* Only show "Back to Home" link if we are NOT on the OTP form */}
        {!isOtpSent && <Link to="/" className="back-link">← Back to Home</Link>}
        
        {/* Change title based on which form is active */}
        <h2>{isOtpSent ? 'Almost There!' : 'Create Account'}</h2>
        
        {/* STEP 4: The Conditional Logic.
          This is the "magic" part.
          - If 'isOtpSent' is true, call renderOtpForm().
          - Otherwise (else), call renderSignupForm().
        */}
        {isOtpSent ? renderOtpForm() : renderSignupForm()}
        
        {/* Only show "Already have account" link if we are NOT on the OTP form */}
        {!isOtpSent && (
          <div className="signup-link">
            <p>Already have an account? <Link to="/login">Log in here</Link></p>
          </div>
        )}
        
      </div>
    </div>
  );
}

export default SignupPage;
