import React, { useState, useEffect } from 'react';
// 1. 'useNavigate' aur 'useLocation' ko import karein
import { BrowserRouter, Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';

// Humare saare pages
import HomePage from './HomePage';
import LoginPage from './LoginPage';
import SignupPage from './SignupPage';
import ContactPage from './ContactPage';
import HelpPage from './HelpPage';
import ReportItemPage from './ReportItemPage';
import ItemsListPage from './ItemsListPage';
import AdminPanel from './AdminPanel';

// Humari main CSS file
import './App.css'; 

// Yeh humara main App component hai
function App() {
  return (
    // Router (BrowserRouter) ko sabse bahar rakhein
    <BrowserRouter>
      {/* Ab saara logic AppContent component mein hoga */}
      <AppContent /> 
    </BrowserRouter>
  );
}

// 2. Humne yeh naya component banaya
// Kyunki yeh BrowserRouter ke andar hai, yeh hooks (useNavigate, etc.) use kar sakta hai
function AppContent() {

  // 3. Ek state banayi jo yaad rakhegi ki user login hai ya nahi
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();
  const location = useLocation(); // Yeh humein batata hai ki hum kis URL par hain

  // 4. Yeh function har baar check karega jab page badlega
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
    } else {
      setIsLoggedIn(false);
    }
  }, [location]); // Yeh [location] ke kaaran har page change par chalta hai

  // 5. Logout ka function
  const handleLogout = () => {
    localStorage.removeItem('token'); // Token ko memory se hata do
    setIsLoggedIn(false); // State ko update karo
    navigate('/login'); // Login page par bhej do
  };

  return (
    // Empty tag <> taaki hum ek se zyada cheezein return kar sakein
    <>
      {/* 1. TOP TITLE (waisa hi hai) */}
      <header className="top-header">
        <h1>Lost and Found</h1>
      </header>

      {/* 2. NAVIGATION BAR (Yahan logic badla hai) */}
      <nav className="navbar">
        <div className="nav-left">
          <Link to="/">Home</Link>
          <a href="/#about-portal">About</a>
          <Link to="/contact">Contact Us</Link>
          <Link to="/help">Help</Link>
        </div>

        {/* --- YEH HAI ASLI BADLAAV --- */}
        <div className="nav-right">
          {isLoggedIn ? (
            // Agar user Logged In hai:
            <>
              {/* Hum baad mein yahan Admin link bhi daal sakte hain */}
              <a href="#" onClick={handleLogout} className="logout-link">Logout</a>
            </>
          ) : (
            // Agar user Logged Out hai:
            <>
              <Link to="/login">Login</Link>
              <Link to="/signup">Signup</Link>
            </>
          )}
        </div>
        {/* --- BADLAAV KHATAM --- */}
      </nav>

      {/* --- Neeche sab waisa hi hai --- */}
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<LoginPage />} />
        <Route path="/signup" element={<SignupPage />} />
        <Route path="/contact" element={<ContactPage />} />
        <Route path="/help" element={<HelpPage />} />
        <Route path="/report-item" element={<ReportItemPage />} />
        <Route path="/view-items" element={<ItemsListPage />} />
        <Route path="/admin" element={<AdminPanel />} />
      </Routes>
    </>
  );
}

export default App;
