import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';
import jwtDecode from 'jwt-decode';

// (Baaki saare page imports waise hi rahenge)
import HomePage from './HomePage';
import LoginPage from './LoginPage';
// ... baaki saare pages ...
import AdminPanel from './AdminPanel';

import './App.css'; 

function App() {
  return (
    <BrowserRouter>
      <AppContent /> 
    </BrowserRouter>
  );
}

function AppContent() {

  // 2. Ek naya state 'userRole' ke liye banaya
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null); // <-- YEH NAYA HAI

  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        // 3. Token ko decode (padh) rahe hain
        const decodedToken = jwtDecode(token);
        setUserRole(decodedToken.user.role); // <-- Token se role nikaala
        setIsLoggedIn(true);
      } catch (error) {
        // Agar token galat hai toh logout kar do
        console.error("Invalid token:", error);
        localStorage.removeItem('token');
        setIsLoggedIn(false);
        setUserRole(null);
      }
    } else {
      setIsLoggedIn(false);
      setUserRole(null);
    }
  }, [location]); // Yeh har page change par check karega

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setUserRole(null); // <-- Role ko bhi reset kiya
    navigate('/login');
  };

  return (
    <>
      {/* --- TOP HEADER (waisa hi hai) --- */}
      <header className="top-header">
        <h1>Lost and Found</h1>
      </header>

      {/* --- NAVIGATION BAR (Yahan badlaav hai) --- */}
      <nav className="navbar">
        <div className="nav-left">
          <Link to="/">Home</Link>
          <a href="/#about-portal">About</a>
          <Link to="/contact">Contact Us</Link>
          <Link to="/help">Help</Link>
        </div>

        <div className="nav-right">
          {isLoggedIn ? (
            // Agar user Logged In hai:
            <>
              {/* --- 4. YEH HAI NAYA ADMIN LINK --- */}
              {userRole === 'admin' && (
                <Link to="/admin" className="admin-link">Admin Panel</Link>
              )}
              {/* ---------------------------------- */}
              <a href="#" onClick={handleLogout} className="logout-link">Logout</a>
            </>
          ) : (
            // Agar user Logged Out hai:
            <>
              <Link to="/login">Login</Link>
              <Link to="/signup">Signup</Link>
            </>
          )}
        </div>
      </nav>

      {/* --- Routes (waise hi hain) --- */}
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<LoginPage />} />
        {/* ... baaki saare routes ... */}
        <Route path="/admin" element={<AdminPanel />} />
      </Routes>
    </>
  );
}

export default App;
